import React, { useState } from "react";
import "./home.css";
import Delete from "../../images/delete.png";
import Update from "../../images/update.png";
import Done from "../../images/done.png";
// import { todos as todosData } from "../../images/data";

export default function Home({userId}) {
  const [todos, setTodos] = useState([]);

  const deleteTodo = (id) => {
    fetch(`http://localhost:3000/todos/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.status === 204) {
          setTodos((prevTodos) =>
            prevTodos.filter((todo) => todo.id !== id)
          );
          console.log('Delete todo successful');
        } else {
          throw new Error('Delete todo failed');
        }
      })
      .catch((error) => console.error(error));
  };


  const updateTodo = (id) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) => (todo.id === id ? { ...todo, editing: true } : todo))
    );
    console.log("update Todo successful");
  };

  const doneEditingTodo = (id) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, editing: false } : todo
      )
    );
    console.log("done editing Todo successful");
  };

  const refresh = () => {
    console.log()
    fetch(`http://localhost:3000/todos?user_id=${userId}`)
      .then((response) => response.json())
      .then((data) => {
        const todosWithId = data.map((todo, index) => ({ ...todo, id: index + 1 }));
        setTodos(todosWithId);
        console.log("refreshed todos:", todosWithId);
      })
      .catch((error) => console.error(error));
  };

  const handleInputChange = (event, id, key) => {
    setTodos(prevTodos => {
      const foundIndex = prevTodos.findIndex((todo) => todo.id === id);
      const updatedTodos = [...prevTodos];
      updatedTodos[foundIndex][key] = event.target.value;
      return updatedTodos;
    });
  }

  return (
    <div className="main">
      <h1>TO-DOs</h1>
      <button onClick={refresh}>refresh</button>

      {todos.length > 0 ? (
        todos.map((todo) => (
          <div className="todos" key={todo.id}>
            {/* <p>{todo.id}</p> */}
            <input
              className="title"
              value={todo.title}
              type="text"
              disabled={!todo.editing}
              onChange={(e) => handleInputChange(e, todo.id, "title")}
            />
            <input
              className="summary"
              type="text"
              value={todo.summary}
              disabled={!todo.editing}
              onChange={(e) => handleInputChange(e, todo.id, "summary")}
            />
            <div className="button-container">
              {todo.editing ? (
                <button id={`done-${todo.id}`} onClick={() => doneEditingTodo(todo.id)}>
                  <img className="icons" id={`done-${todo.id}`} src={Done} alt="done-icon" />
                </button>
              ) : (
                <button id={`update-${todo.id}`} onClick={() => updateTodo(todo.id)}>
                  <img className="icons" id={`update-${todo.id}`} src={Update} alt="update-icon" />
                </button>
              )}
              <button id={`delete-${todo.id}`} onClick={() => deleteTodo(todo.id)}>
                <img className="icons" id={`delete-${todo.id}`} src={Delete} alt="delete-icon" />
              </button>
            </div>
          </div>
        ))
      ) : (
        <div className="no-todos">
          <h2>No Todos</h2>
        </div>
      )}
    </div>
  );
}
