import React, { useState } from "react";

export default function Login() {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const fetchLogin = () =>{
    console.log("logging in: ")
  }

  const fetchSignUp = () =>{
    console.log("signing up: ")

  }

  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleShowPasswordClick = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!event.target.checkValidity()) {
      // If the form is not valid, show an error message and return
      alert("Please fill up all required fields.");
      return;
    }

    // If all fields are filled up, check if the passwords match (for sign up)
    if (!isLogin && password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    // If everything is valid, call the correct fetch function
    if (isLogin) {
      fetchLogin();
    } else {
      fetchSignUp();
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center mt-5">
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h1>{isLogin ? "Login" : "Sign Up"}</h1>
              <form onSubmit={handleSubmit} noValidate>
    <div className="mb-3">
      <label className="form-label" htmlFor="username">Username</label>
      <input type="text" className="form-control" id="username" required />
    </div>
    <div className="mb-3">
      <label className="form-label" htmlFor="email">Email</label>
      <input type="email" className="form-control" id="email" required />
    </div>
    <div className="mb-3">
      <label className="form-label" htmlFor="password">Password</label>
      <div className="input-group">
        <input
          type={showPassword ? "text" : "password"}
          className="form-control"
          id="password"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <button
          className="btn btn-outline-secondary"
          type="button"
          onClick={handleShowPasswordClick}
        >
          {showPassword ? "Hide" : "Show"}
        </button>
      </div>
    </div>
    {isLogin ? null : (
      <div>
        <div className="mb-3">
          <label className="form-label" htmlFor="confirmPassword">Confirm Password</label>
          <div className="input-group">
            <input
              type={showPassword ? "text" : "password"}
              className="form-control"
              id="confirmPassword"
              value={confirmPassword}
              onChange={handleConfirmPasswordChange}
              required
            />
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={handleShowPasswordClick}
            >
              {showPassword ? "Hide" : "Show"}
            </button>
          </div>
        </div>
      </div>
    )}
    <button
      type="submit"
      className="btn btn-primary"
      disabled={!event.target.checkValidity()}
    >
      {isLogin ? "Login" : "Sign Up"}
    </button>
  </form>
              <hr />
              <p>
                {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
                <button className="btn btn-link" onClick={toggleForm}>
                  {isLogin ? "Sign up here" : "Login here"}
                </button>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
