import React, {useState} from "react";
import "./home.css";
import Delete from "../../images/delete.png";
import Update from "../../images/update.png";

export default function Home() {
    const [todos, setTodos] = useState([
        {
          id: 1,
          title: "Go for milk",
          summary: "Closes at 10pm",
        },
        {
          id: 2,
          title: "Buy groceries",
          summary: "Need to get fruits and vegetables",
        },
        {
          id: 3,
          title: "Clean the house",
          summary: "Vacuum the floors and dust the furniture",
        },
        {
          id: 4,
          title: "Finish project",
          summary: "Complete the remaining tasks by Friday",
        },
      ]);


  const deleteTodo = () => {
    console.log("delete Todo successful");
    todos
    console.log(todos);
  };
  const updateTodo = () => {
    console.log("update Todo successful");
  };


  return (
    <div className="main">
      <h1>TO-DOs</h1>

      <div className="todos">
        <p>1</p>
        <input className="title" value="Go for milk" type="text" disabled />
        <input
          className="summary"
          type="text"
          value="Closes at 10pm"
          disabled
        />
        <div className="button-container">
          <button  id="update" onClick={updateTodo}>
            <img className="icons" id="update" src={Update} alt="update-icon" />
          </button>
          <button id="delete"  onClick={deleteTodo}>
            <img className="icons" id="delete" src={Delete} alt="delete-icon" />
          </button>
        </div>
      </div>

      <div className="todos">
        <p>2</p>
        <input className="title" value="cook food" type="text" disabled />
        <input
          className="summary"
          type="text"
          value="do it by tonight"
          disabled
        />
        <div className="button-container">
        <button id="update"  onClick={updateTodo}>
          <img className="icons" id="update" src={Update} alt="delete-icon" />
        </button>
        <button id="delete" onClick={deleteTodo}>
          <img className="icons" id="delete" src={Delete} alt="delete-icon" />
        </button>
        </div>
      </div>
    </div>
  );
}
