import React, {useState} from "react";
import "./home.css";
import Delete from "../../images/delete.png";
import Update from "../../images/update.png";
export default function Home() {
    const [todos, setTodos] = useState([
      {
        id: 1,
        title: "Go for milk",
        summary: "Closes at 10pm",
      },
      {
        id: 2,
        title: "Buy groceries",
        summary: "Need to get fruits and vegetables",
      },
      {
        id: 3,
        title: "Clean the house",
        summary: "Vacuum the floors and dust the furniture",
      },
      {
        id: 4,
        title: "Finish project",
        summary: "Complete the remaining tasks by Friday",
      },
    ]);

    const deleteTodo = (id) => {
      // filter out the todo with the specified id
      const newTodos = todos.filter((todo) => todo.id !== id);
      // update the state with the new todos array
      setTodos(newTodos);
    };

    const updateTodo = (id) => {
      console.log("update Todo successful");
    };

    return (
      <div className="main">
        <h1>TO-DOs</h1>

        {todos.map((todo) => (
          <div className="todos" key={todo.id}>
            <p>{todo.id}</p>
            <input className="title" value={todo.title} type="text" disabled />
            <input className="summary" type="text" value={todo.summary} disabled />
            <div className="button-container">
              <button id="update" onClick={() => updateTodo(todo.id)}>
                <img className="icons" id="update" src={Update} alt="update-icon" />
              </button>
              <button id="delete" onClick={() => deleteTodo(todo.id)}>
                <img className="icons" id="delete" src={Delete} alt="delete-icon" />
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  }
