import React, { useState } from "react";
import "./home.css";
import Delete from "../../images/delete.png";
import Update from "../../images/update.png";
import { todos as todosData } from "../../images/data";

export default function Home() {
  const [todos, setTodos] = useState(todosData);
  const [isEditMode, setIsEditMode] = useState(false);
  const [tempTitle, setTempTitle] = useState("");
  const [tempSummary, setTempSummary] = useState("");

  const deleteTodo = (id) => {
    setTodos((prevTodos) => {
      return prevTodos
        .filter((todo) => todo.id !== id)
        .map((todo, index) => {
          return {
            ...todo,
            id: index + 1,
          };
        });
    });
    console.log("delete Todo successful");
  };

  const editTodo = (id) => {
    const todoToUpdate = todos.find((todo) => todo.id === id);
    setTempTitle(todoToUpdate.title);
    setTempSummary(todoToUpdate.summary);
    setIsEditMode(true);
  };

  const updateTodo = (id) => {
    setTodos((prevTodos) => {
      return prevTodos.map((todo) => {
        if (todo.id === id) {
          return {
            ...todo,
            title: tempTitle,
            summary: tempSummary,
          };
        }
        return todo;
      });
    });
    setIsEditMode(false);
    console.log("update Todo successful");
  };

  const refresh = () => {
    console.log("refreshed successfully");
  };

  return (
    <div className="main">
      <h1>TO-DOs</h1>
      <button onClick={() => refresh()}>refresh</button>

      {todos.map((todo) => (
        <div className="todos" key={todo.id}>
          <p>{todo.id}</p>
          {isEditMode ? (
            <input
              className="title"
              value={tempTitle}
              onChange={(e) => setTempTitle(e.target.value)}
              type="text"
            />
          ) : (
            <input className="title" value={todo.title} type="text" readOnly />
          )}
          {isEditMode ? (
            <input
              className="summary"
              value={tempSummary}
              onChange={(e) => setTempSummary(e.target.value)}
              type="text"
            />
          ) : (
            <input
              className="summary"
              value={todo.summary}
              type="text"
              readOnly
            />
          )}
          <div className="button-container">
            {isEditMode ? (
              <button id="done" onClick={() => updateTodo(todo.id)}>
                Done
              </button>
            ) : (
              <button id="update" onClick={() => editTodo(todo.id)}>
                <img
                  className="icons"
                  id="update"
                  src={Update}
                  alt="update-icon"
                />
              </button>
            )}
            <button id="delete" onClick={() => deleteTodo(todo.id)}>
              <img
                className="icons"
                id="delete"
                src={Delete}
                alt="delete-icon"
              />
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
